import hljs from 'highlight.js';
import MarkdownIt from 'markdown-it';
import type Token from 'markdown-it/lib/token';
import {escape} from 'underscore';

import {replaceInRange} from './StringUtil';

import {QualifiedId} from '@wireapp/api-client/src/user';
import type {MentionEntity} from '../message/MentionEntity';

interface MentionText {
  domain: string | null;
  isSelfMentioned: boolean;
  text: string;
  userId: string;
}

interface MarkdownItWithOptions extends MarkdownIt {
  options: MarkdownIt.Options;
}

// Note: We are using "Underscore.js" to escape HTML in the original message
const markdownit = new MarkdownIt('zero', {
  breaks: true,
  html: false,
  langPrefix: 'lang-',
  linkify: true,
}).enable(['autolink', 'backticks', 'code', 'emphasis', 'escape', 'fence', 'heading', 'link', 'linkify', 'newline']);

const originalFenceRule = markdownit.renderer.rules.fence;

markdownit.renderer.rules.fence = (tokens, idx, options, env, self) => {
  const highlighted = originalFenceRule(tokens, idx, options, env, self);
  tokens[idx].map[1] += 1;
  return highlighted.replace(/\n$/, '');
};

markdownit.renderer.rules.heading_open = () => '<div class="md-heading">';
markdownit.renderer.rules.heading_close = () => '</div>';

markdownit.renderer.rules.softbreak = () => '<br>';
markdownit.renderer.rules.hardbreak = () => '<br>';
markdownit.renderer.rules.paragraph_open = (tokens, idx) => {
  const [position] = tokens[idx].map;
  const previousWithMap = tokens
    .slice(0, idx)
    .reverse()
    .find(({map}) => map?.length);
  const previousPosition = previousWithMap ? previousWithMap.map[1] - 1 : 0;
  const count = position - previousPosition;
  return '<br>'.repeat(Math.max(count, 0));
};
markdownit.renderer.rules.paragraph_close = () => '';

// https://github.com/markdown-it/markdown-it/issues/458#issuecomment-401221267
function modifyMarkdownLinks(markdown: string): string {
  const matches = markdownit.linkify.match(markdown);
  if (!matches || matches.length === 0) {
    return markdown;
  }
  const result = [];
  let prevEndIndex = 0;
  for (const match of matches) {
    const startsWithProto = /^https?:\/\//i.test(match.raw);
    const noStartBracket = match.index === 0 || markdown[match.index - 1] !== '<';
    const noEndBracket = match.lastIndex === markdown.length || markdown[match.lastIndex] !== '>';
    const shouldInsertBrackets = startsWithProto && noStartBracket && noEndBracket;

    result.push(markdown.slice(prevEndIndex, match.index));
    result.push(shouldInsertBrackets ? `<${match.raw}>` : match.raw);
    prevEndIndex = match.lastIndex;
  }
  result.push(markdown.slice(prevEndIndex));
  return result.join('');
}

markdownit.normalizeLinkText = text => text;

export const renderMessage = (message: string, selfId: QualifiedId | null, mentionEntities: MentionEntity[] = []) => {
  const createMentionHash = (mention: MentionEntity) => `@@${window.btoa(JSON.stringify(mention)).replace(/=/g, '')}`;
  const renderMention = (mentionData: MentionText) => {
    const elementClasses = mentionData.isSelfMentioned ? ' self-mention' : '';
    let elementAttributes = mentionData.isSelfMentioned
      ? ' data-uie-name="label-self-mention"'
      : ` data-uie-name="label-other-mention" data-user-id="${mentionData.userId}"`;
    if (!mentionData.isSelfMentioned && mentionData.domain) {
      elementAttributes += ` data-user-domain="${mentionData.domain}"`;
    }

    const mentionText = mentionData.text.replace(/^@/, '');
    const content = `<span class="mention-at-sign">@</span>${escape(mentionText)}`;
    return `<span class="message-mention${elementClasses}"${elementAttributes}>${content}</span>`;
  };

  const mentionTexts: Record<string, MentionText> = {};

  let mentionlessText = mentionEntities
    .slice()
    // sort mentions to start with the latest mention first (in order not to have to recompute the index every time we modify the original text)
    .sort((mention1, mention2) => mention2.startIndex - mention1.startIndex)
    .reduce((strippedText, mention) => {
      const mentionText = message.slice(mention.startIndex, mention.startIndex + mention.length);
      const mentionKey = createMentionHash(mention);
      mentionTexts[mentionKey] = {
        domain: mention.domain,
        isSelfMentioned: !!selfId && mention.targetsUser(selfId),
        text: mentionText,
        userId: mention.userId,
      };
      return replaceInRange(strippedText, mentionKey, mention.startIndex, mention.startIndex + mention.length);
    }, message);

  markdownit.set({
    highlight: function (code): string {
      const containsMentions = mentionEntities.some(mention => {
        const hash = createMentionHash(mention);
        return code.includes(hash);
      });
      if (containsMentions) {
        // disable code highlighting if there is a mention in there
        // highlighting will be wrong anyway because this is not valid code
        return code;
      }
      return hljs.highlightAuto(code).value;
    },
  });

  markdownit.renderer.rules.link_open = (tokens, idx, options, env, self) => {
    const cleanString = (hashedString: string) =>
      escape(
        Object.entries(mentionTexts).reduce(
          (text, [mentionHash, mention]) => text.replace(mentionHash, mention.text),
          hashedString,
        ),
      );
    const link = tokens[idx];
    const href = cleanString(link.attrGet('href'));
    const isEmail = href.startsWith('mailto:');
    const isWireDeepLink = href.toLowerCase().startsWith('wire://');
    const nextToken = tokens[idx + 1];
    const text = nextToken?.type === 'text' ? nextToken.content : '';

    if (!href || !text.trim()) {
      nextToken.content = '';
      const closeToken = tokens.slice(idx).find(token => token.type === 'link_close');
      closeToken.type = 'text';
      closeToken.content = '';
      return `[${cleanString(text)}](${cleanString(href)})`;
    }
    if (isEmail) {
      link.attrPush(['data-email-link', 'true']);
    } else {
      link.attrPush(['target', '_blank']);
      link.attrPush(['rel', 'nofollow noopener noreferrer']);
    }
    if (!isWireDeepLink && !['autolink', 'linkify'].includes(link.markup)) {
      const title = link.attrGet('title');
      if (title) {
        link.attrSet('title', cleanString(title));
      }
      link.attrSet('href', cleanString(href));
      if (nextToken?.type === 'text') {
        nextToken.content = text;
      }
      link.attrPush(['data-md-link', 'true']);
      link.attrPush(['data-uie-name', 'markdown-link']);
    }
    if (isWireDeepLink) {
      link.attrPush(['data-uie-name', 'wire-deep-link']);
    }
    if (link.markup === 'linkify') {
      nextToken.content = encodeURI(nextToken.content);
    }
    return self.renderToken(tokens, idx, options);
  };
  const originalTokens = markdownit.parse(mentionlessText, {});
  mentionlessText = modifyMarkdownLinks(mentionlessText);
  const modifiedLinksTokens = markdownit.parse(mentionlessText, {});
  const fixCodeTokens = (modifiedTokens: Token[], originalTokens: Token[]) =>
    modifiedTokens.map((modifiedToken, index) => {
      const originalToken = originalTokens[index];
      if (modifiedToken.tag === 'code') {
        return originalToken;
      }
      if (modifiedToken.children) {
        modifiedToken.children = fixCodeTokens(modifiedToken.children, originalToken.children);
      }
      return modifiedToken;
    });
  const fixedTokens = fixCodeTokens(modifiedLinksTokens, originalTokens);
  mentionlessText = markdownit.renderer.render(fixedTokens, (markdownit as MarkdownItWithOptions).options, {});
  // Remove <br> and \n if it is the last thing in a message
  mentionlessText = mentionlessText.replace(/(<br>|\n)*$/, '');

  const parsedText = Object.keys(mentionTexts).reduce((text, mentionHash) => {
    const mentionMarkup = renderMention(mentionTexts[mentionHash]);

    return text.replace(mentionHash, mentionMarkup);
  }, mentionlessText);
  return parsedText;
};

export const getRenderedTextContent = (text: string): string => {
  const renderedMessage = renderMessage(text, {domain: '', id: ''});
  const messageWithLinebreaks = renderedMessage.replace(/<br>/g, '\n');
  const strippedMessage = messageWithLinebreaks.replace(/<.+?>/g, '');
  return markdownit.utils.unescapeAll(strippedMessage);
};

import type {
  DeepMergeBuiltInMetaData,
  DeepMergeHKT,
  DeepMergeArraysDefaultHKT,
  DeepMergeMergeFunctionsDefaultURIs,
  DeepMergeMapsDefaultHKT,
  DeepMergeMergeFunctionsURIs,
  DeepMergeOptions,
  DeepMergeRecordsDefaultHKT,
  DeepMergeSetsDefaultHKT,
  DeepMergeMergeFunctionUtils,
  GetDeepMergeMergeFunctionsURIs,
} from "./types";
import {
  getIterableOfIterables,
  getKeys,
  getObjectType,
  ObjectType,
  objectHasProperty,
} from "./utils";

const defaultMergeFunctions = {
  mergeMaps: defaultMergeMaps,
  mergeSets: defaultMergeSets,
  mergeArrays: defaultMergeArrays,
  mergeRecords: defaultMergeRecords,
  mergeOthers: leaf,
} as const;

/**
 * Special values that tell deepmerge-ts to perform a certain action.
 */
const actions = {
  defaultMerge: Symbol("deepmerge-ts: default merge"),
  skip: Symbol("deepmerge-ts: skip"),
} as const;

/**
 * The default function to update meta data.
 */
function defaultMetaDataUpdater<M>(
  previousMeta: M,
  metaMeta: DeepMergeBuiltInMetaData
): DeepMergeBuiltInMetaData {
  return metaMeta;
}

/**
 * The default merge functions.
 */
export type DeepMergeMergeFunctionsDefaults = typeof defaultMergeFunctions;

/**
 * Deeply merge objects.
 *
 * @param objects - The objects to merge.
 */
export function deepmerge<Ts extends Readonly<ReadonlyArray<unknown>>>(
  ...objects: readonly [...Ts]
): DeepMergeHKT<
  Ts,
  DeepMergeMergeFunctionsDefaultURIs,
  DeepMergeBuiltInMetaData
> {
  return deepmergeCustom({})(...objects) as DeepMergeHKT<
    Ts,
    DeepMergeMergeFunctionsDefaultURIs,
    DeepMergeBuiltInMetaData
  >;
}

/**
 * Deeply merge two or more objects using the given options.
 *
 * @param options - The options on how to customize the merge function.
 */
export function deepmergeCustom<
  PMF extends Partial<DeepMergeMergeFunctionsURIs>
>(
  options: DeepMergeOptions<DeepMergeBuiltInMetaData, DeepMergeBuiltInMetaData>
): <Ts extends ReadonlyArray<unknown>>(
  ...objects: Ts
) => DeepMergeHKT<
  Ts,
  GetDeepMergeMergeFunctionsURIs<PMF>,
  DeepMergeBuiltInMetaData
>;

/**
 * Deeply merge two or more objects using the given options and meta data.
 *
 * @param options - The options on how to customize the merge function.
 * @param rootMetaData - The meta data passed to the root items' being merged.
 */
export function deepmergeCustom<
  PMF extends Partial<DeepMergeMergeFunctionsURIs>,
  MetaData,
  MetaMetaData extends DeepMergeBuiltInMetaData = DeepMergeBuiltInMetaData
>(
  options: DeepMergeOptions<MetaData, MetaMetaData>,
  rootMetaData?: MetaData
): <Ts extends ReadonlyArray<unknown>>(
  ...objects: Ts
) => DeepMergeHKT<Ts, GetDeepMergeMergeFunctionsURIs<PMF>, MetaData>;

export function deepmergeCustom<
  PMF extends Partial<DeepMergeMergeFunctionsURIs>,
  MetaData,
  MetaMetaData extends DeepMergeBuiltInMetaData
>(
  options: DeepMergeOptions<MetaData, MetaMetaData>,
  rootMetaData?: MetaData
): <Ts extends ReadonlyArray<unknown>>(
  ...objects: Ts
) => DeepMergeHKT<Ts, GetDeepMergeMergeFunctionsURIs<PMF>, MetaData> {
  /**
   * The type of the customized deepmerge function.
   */
  type CustomizedDeepmerge = <Ts extends ReadonlyArray<unknown>>(
    ...objects: Ts
  ) => DeepMergeHKT<Ts, GetDeepMergeMergeFunctionsURIs<PMF>, MetaData>;

  const utils: DeepMergeMergeFunctionUtils<MetaData, MetaMetaData> = getUtils(
    options,
    customizedDeepmerge as CustomizedDeepmerge
  );

  /**
   * The customized deepmerge function.
   */
  function customizedDeepmerge(...objects: ReadonlyArray<unknown>) {
    return mergeUnknowns<
      ReadonlyArray<unknown>,
      typeof utils,
      GetDeepMergeMergeFunctionsURIs<PMF>,
      MetaData,
      MetaMetaData
    >(objects, utils, rootMetaData);
  }

  return customizedDeepmerge as CustomizedDeepmerge;
}

/**
 * The the full options with defaults apply.
 *
 * @param options - The options the user specified
 */
function getUtils<M, MM extends DeepMergeBuiltInMetaData>(
  options: DeepMergeOptions<M, MM>,
  customizedDeepmerge: DeepMergeMergeFunctionUtils<M, MM>["deepmerge"]
): DeepMergeMergeFunctionUtils<M, MM> {
  return {
    defaultMergeFunctions,
    mergeFunctions: {
      ...defaultMergeFunctions,
      ...Object.fromEntries(
        Object.entries(options)
          .filter(([key, option]) =>
            Object.prototype.hasOwnProperty.call(defaultMergeFunctions, key)
          )
          .map(([key, option]) =>
            option === false ? [key, leaf] : [key, option]
          )
      ),
    } as DeepMergeMergeFunctionUtils<M, MM>["mergeFunctions"],
    metaDataUpdater: (options.metaDataUpdater ??
      defaultMetaDataUpdater) as unknown as DeepMergeMergeFunctionUtils<
      M,
      MM
    >["metaDataUpdater"],
    deepmerge: customizedDeepmerge,
    useImplicitDefaultMerging: options.enableImplicitDefaultMerging ?? false,
    actions,
  };
}

/**
 * Merge unknown things.
 *
 * @param values - The values.
 */
function mergeUnknowns<
  Ts extends ReadonlyArray<unknown>,
  U extends DeepMergeMergeFunctionUtils<M, MM>,
  MF extends DeepMergeMergeFunctionsURIs,
  M,
  MM extends DeepMergeBuiltInMetaData
>(values: Ts, utils: U, meta: M | undefined): DeepMergeHKT<Ts, MF, M> {
  if (values.length === 0) {
    return undefined as DeepMergeHKT<Ts, MF, M>;
  }
  if (values.length === 1) {
    return mergeOthers<U, M, MM>(values, utils, meta) as DeepMergeHKT<
      Ts,
      MF,
      M
    >;
  }

  const type = getObjectType(values[0]);

  // eslint-disable-next-line functional/no-conditional-statement -- add an early escape for better performance.
  if (type !== ObjectType.NOT && type !== ObjectType.OTHER) {
    // eslint-disable-next-line functional/no-loop-statement -- using a loop here is more performant than mapping every value and then testing every value.
    for (let mutableIndex = 1; mutableIndex < values.length; mutableIndex++) {
      if (getObjectType(values[mutableIndex]) === type) {
        continue;
      }

      return mergeOthers<U, M, MM>(values, utils, meta) as DeepMergeHKT<
        Ts,
        MF,
        M
      >;
    }
  }

  switch (type) {
    case ObjectType.RECORD:
      return mergeRecords<U, MF, M, MM>(
        values as ReadonlyArray<Readonly<Record<PropertyKey, unknown>>>,
        utils,
        meta
      ) as DeepMergeHKT<Ts, MF, M>;

    case ObjectType.ARRAY:
      return mergeArrays<U, M, MM>(
        values as ReadonlyArray<Readonly<ReadonlyArray<unknown>>>,
        utils,
        meta
      ) as DeepMergeHKT<Ts, MF, M>;

    case ObjectType.SET:
      return mergeSets<U, M, MM>(
        values as ReadonlyArray<Readonly<ReadonlySet<unknown>>>,
        utils,
        meta
      ) as DeepMergeHKT<Ts, MF, M>;

    case ObjectType.MAP:
      return mergeMaps<U, M, MM>(
        values as ReadonlyArray<Readonly<ReadonlyMap<unknown, unknown>>>,
        utils,
        meta
      ) as DeepMergeHKT<Ts, MF, M>;

    default:
      return mergeOthers<U, M, MM>(values, utils, meta) as DeepMergeHKT<
        Ts,
        MF,
        M
      >;
  }
}

/**
 * Merge records.
 *
 * @param values - The records.
 */
function mergeRecords<
  U extends DeepMergeMergeFunctionUtils<M, MM>,
  MF extends DeepMergeMergeFunctionsURIs,
  M,
  MM extends DeepMergeBuiltInMetaData
>(
  values: ReadonlyArray<Readonly<Record<PropertyKey, unknown>>>,
  utils: U,
  meta: M | undefined
) {
  const result = utils.mergeFunctions.mergeRecords(values, utils, meta);

  if (
    result === actions.defaultMerge ||
    (utils.useImplicitDefaultMerging &&
      result === undefined &&
      utils.mergeFunctions.mergeRecords !==
        utils.defaultMergeFunctions.mergeRecords)
  ) {
    return utils.defaultMergeFunctions.mergeRecords<
      ReadonlyArray<Readonly<Record<PropertyKey, unknown>>>,
      U,
      MF,
      M,
      MM
    >(values, utils, meta);
  }

  return result;
}

/**
 * Merge arrays.
 *
 * @param values - The arrays.
 */
function mergeArrays<
  U extends DeepMergeMergeFunctionUtils<M, MM>,
  M,
  MM extends DeepMergeBuiltInMetaData
>(
  values: ReadonlyArray<Readonly<ReadonlyArray<unknown>>>,
  utils: U,
  meta: M | undefined
) {
  const result = utils.mergeFunctions.mergeArrays(values, utils, meta);

  if (
    result === actions.defaultMerge ||
    (utils.useImplicitDefaultMerging &&
      result === undefined &&
      utils.mergeFunctions.mergeArrays !==
        utils.defaultMergeFunctions.mergeArrays)
  ) {
    return utils.defaultMergeFunctions.mergeArrays(values);
  }
  return result;
}

/**
 * Merge sets.
 *
 * @param values - The sets.
 */
function mergeSets<
  U extends DeepMergeMergeFunctionUtils<M, MM>,
  M,
  MM extends DeepMergeBuiltInMetaData
>(
  values: ReadonlyArray<Readonly<ReadonlySet<unknown>>>,
  utils: U,
  meta: M | undefined
) {
  const result = utils.mergeFunctions.mergeSets(values, utils, meta);

  if (
    result === actions.defaultMerge ||
    (utils.useImplicitDefaultMerging &&
      result === undefined &&
      utils.mergeFunctions.mergeSets !== utils.defaultMergeFunctions.mergeSets)
  ) {
    return utils.defaultMergeFunctions.mergeSets(values);
  }
  return result;
}

/**
 * Merge maps.
 *
 * @param values - The maps.
 */
function mergeMaps<
  U extends DeepMergeMergeFunctionUtils<M, MM>,
  M,
  MM extends DeepMergeBuiltInMetaData
>(
  values: ReadonlyArray<Readonly<ReadonlyMap<unknown, unknown>>>,
  utils: U,
  meta: M | undefined
) {
  const result = utils.mergeFunctions.mergeMaps(values, utils, meta);

  if (
    result === actions.defaultMerge ||
