			tag = "textarea";
		}

		if (doc.all) {
			ele = doc.createElement("<" + tag + " name=\"" + name + "\" />");
		} else {
			ele = doc.createElement(tag);
			ele.setAttribute("name", name);
		}
		ele.setAttribute("value", value);
		form.appendChild(ele);
		ele = null;
