/**
 * Provides an "event like" system to parse strings of HTML data.
 *
 *		var parser = new CKEDITOR.htmlParser();
 *		parser.onTagOpen = function( tagName, attributes, selfClosing ) {
 *			alert( tagName );
 *		};
 *		parser.parse( '<p>Some <b>text</b>.</p>' ); // Alerts 'p', 'b'.
 *
 * @class
 * @constructor Creates a htmlParser class instance.
 */
CKEDITOR.htmlParser = function() {
	this._ = {
		htmlPartsRegex: /<(?:(?:\/([^>]+)>)|(?:!--([\S|\s]*?)--!?>)|(?:([^\/\s>]+)((?:\s+[\w\-:.]+(?:\s*=\s*?(?:(?:"[^"]*")|(?:'[^']*')|[^\s"'\/>]+))?)*)[\S\s]*?(\/?)>))/g
	};
};

( function() {
	var attribsRegex = /([\w\-:.]+)(?:(?:\s*=\s*(?:(?:"([^"]*)")|(?:'([^']*)')|([^\s>]+)))|(?=\s|$))/g,
		emptyAttribs = { checked: 1, compact: 1, declare: 1, defer: 1, disabled: 1, ismap: 1, multiple: 1, nohref: 1, noresize: 1, noshade: 1, nowrap: 1, readonly: 1, selected: 1 };

	CKEDITOR.htmlParser.prototype = {
		/**
		 * Function to be fired when a tag opener is found. This function
		 * should be overriden when using this class.
		 *
		 *		var parser = new CKEDITOR.htmlParser();
		 *		parser.onTagOpen = function( tagName, attributes, selfClosing ) {
		 *			alert( tagName ); // e.g. 'b'
		 *		} );
		 *		parser.parse( '<!-- Example --><b>Hello</b>' );
		 *
		 * @param {String} tagName The tag name. The name is guarantted to be lowercased.
		 * @param {Object} attributes An object containing all tag attributes. Each
		 * property in this object represent and attribute name and its value is the attribute value.
		 * @param {Boolean} selfClosing `true` if the tag closes itself, false if the tag doesn't.
		 */
		onTagOpen: function() {},

		/**
		 * Function to be fired when a tag closer is found. This function
		 * should be overriden when using this class.
		 *
		 *		var parser = new CKEDITOR.htmlParser();
		 *		parser.onTagClose = function( tagName ) {
		 *			alert( tagName ); // 'b'
		 *		} );
		 *		parser.parse( '<!-- Example --><b>Hello</b>' );
		 *
		 * @param {String} tagName The tag name. The name is guarantted to be lowercased.
		 */
		onTagClose: function() {},

		/**
		 * Function to be fired when text is found. This function
		 * should be overriden when using this class.
		 *
		 *		var parser = new CKEDITOR.htmlParser();
		 *		parser.onText = function( text ) {
		 *			alert( text ); // 'Hello'
		 *		} );
		 *		parser.parse( '<!-- Example --><b>Hello</b>' );
		 *
		 * @param {String} text The text found.
		 */
		onText: function() {},

		/**
		 * Function to be fired when CDATA section is found. This function
		 * should be overriden when using this class.
		 *
		 *		var parser = new CKEDITOR.htmlParser();
		 *		parser.onCDATA = function( cdata ) {
		 *			alert( cdata ); // 'var hello;'
		 *		} );
		 *		parser.parse( '<script>var hello;</script>' );
		 *
		 * @param {String} cdata The CDATA been found.
		 */
		onCDATA: function() {},

		/**
		 * Function to be fired when a commend is found. This function
		 * should be overriden when using this class.
		 *
		 *		var parser = new CKEDITOR.htmlParser();
		 *		parser.onComment = function( comment ) {
		 *			alert( comment ); // ' Example '
		 *		} );
		 *		parser.parse( '<!-- Example --><b>Hello</b>' );
		 *
		 * @param {String} comment The comment text.
		 */
		onComment: function() {},

		/**
		 * Parses text, looking for HTML tokens, like tag openers or closers,
		 * or comments. This function fires the onTagOpen, onTagClose, onText
		 * and onComment function during its execution.
		 *
		 *		var parser = new CKEDITOR.htmlParser();
		 *		// The onTagOpen, onTagClose, onText and onComment should be overriden
		 *		// at this point.
		 *		parser.parse( '<!-- Example --><b>Hello</b>' );
		 *
		 * @param {String} html The HTML to be parsed.
		 */
		parse: function( html ) {
			var parts, tagName,
				nextIndex = 0,
				cdata; // The collected data inside a CDATA section.

			while ( ( parts = this._.htmlPartsRegex.exec( html ) ) ) {
				var tagIndex = parts.index;
				if ( tagIndex > nextIndex ) {
					var text = html.substring( nextIndex, tagIndex );

					if ( cdata )
						cdata.push( text );
					else
						this.onText( text );
				}

				nextIndex = this._.htmlPartsRegex.lastIndex;

				// "parts" is an array with the following items:
				//		0 : The entire match for opening/closing tags and comments.
				//		  : Group filled with the tag name for closing tags.
				//		2 : Group filled with the comment text.
				//		3 : Group filled with the tag name for opening tags.
				//		4 : Group filled with the attributes part of opening tags.

				// Closing tag
				if ( ( tagName = parts[ 1 ] ) ) {
					tagName = tagName.toLowerCase();

					if ( cdata && CKEDITOR.dtd.$cdata[ tagName ] ) {
						// Send the CDATA data.
						this.onCDATA( cdata.join( '' ) );
						cdata = null;
					}

					if ( !cdata ) {
						this.onTagClose( tagName );
						continue;
					}
				}

				// If CDATA is enabled, just save the raw match.
				if ( cdata ) {
					cdata.push( parts[ 0 ] );
					continue;
				}

				// Opening tag
				if ( ( tagName = parts[ 3 ] ) ) {
					tagName = tagName.toLowerCase();

					// There are some tag names that can break things, so let's
					// simply ignore them when parsing. (https://dev.ckeditor.com/ticket/5224)
					if ( /="/.test( tagName ) )
						continue;

					var attribs = {},
						attribMatch,
						attribsPart = parts[ 4 ],
						selfClosing = !!parts[ 5 ];

					if ( attribsPart ) {
						while ( ( attribMatch = attribsRegex.exec( attribsPart ) ) ) {
							var attName = attribMatch[ 1 ].toLowerCase(),
								attValue = attribMatch[ 2 ] || attribMatch[ 3 ] || attribMatch[ 4 ] || '';

							if ( !attValue && emptyAttribs[ attName ] )
								attribs[ attName ] = attName;
							else
								attribs[ attName ] = CKEDITOR.tools.htmlDecodeAttr( attValue );
						}
					}

					this.onTagOpen( tagName, attribs, selfClosing );

					// Open CDATA mode when finding the appropriate tags.
					if ( !cdata && CKEDITOR.dtd.$cdata[ tagName ] )
						cdata = [];

					continue;
				}

				// Comment
				if ( ( tagName = parts[ 2 ] ) )
					this.onComment( tagName );
			}

			if ( html.length > nextIndex )
				this.onText( html.substring( nextIndex, html.length ) );
		}
	};
} )();

import React from 'react';
import * as ReactDOM from 'react-dom';
import { Provider } from 'react-redux';

import { sleep } from 'lib/utils';
import { reduxStore } from 'redux/store';
import { overlayRoot } from 'ui/Overlay/Overlay';

import {
    CodeMirrorTooltip,
    ICodeMirrorTooltipProps,
} from './CodeMirrorTooltip';

function mountTooltip(
    node: Element,
    props: Omit<ICodeMirrorTooltipProps, 'hide'>,
    hide: () => void,
    direction: 'up' | 'down' = null
) {
    const tooltipContainer = document.createElement('div');
    tooltipContainer.id = 'Codemirror-tooltip-container';
    overlayRoot.appendChild(tooltipContainer);

    const rect = node.getBoundingClientRect();
    const windowHeight = window.innerHeight;
    if (direction == null) {
        // node is at top half of the screen
        // Show tooltip on bottom
        direction = windowHeight / 2 > rect.top ? 'down' : 'up';
    }

    if (direction === 'down') {
        tooltipContainer.style.top = `${rect.bottom}px`;
    } else {
        // up direction
        tooltipContainer.style.bottom = `${windowHeight - rect.top}px`;
    }

    tooltipContainer.style.left = `${rect.left}px`;
    tooltipContainer.style.opacity = '1';

    const mergedProps = {
        ...props,
        ...{
            hide,
        },
    };
    // Init the react stuff
    ReactDOM.render(
        <Provider store={reduxStore}>
            <CodeMirrorTooltip {...mergedProps} />
        </Provider>,
        tooltipContainer
    );

    return tooltipContainer;
}

function unmountTooltip(tooltipContainer: HTMLDivElement) {
    if (!tooltipContainer.parentNode) {
        return;
    }
    ReactDOM.unmountComponentAtNode(tooltipContainer);
    tooltipContainer.parentNode.removeChild(tooltipContainer);
}

function isHovered(e: Element) {
    return e.parentElement.querySelector(':hover') === e;
}

export async function showTooltipFor(
    nodes: Element[],
    props: Omit<ICodeMirrorTooltipProps, 'hide'>,
    onNodeHide?: () => void,
    direction: 'up' | 'down' = null
) {
    // This sleep is needed to put this code in eventpool which allosw
    // the node itself to actually appear in the browser
    await sleep(1);

    if (!nodes.some((node) => isHovered(node))) {
        if (onNodeHide) {
            onNodeHide();
        }
        return;
    }
    const hoveredNode = nodes.find((node) => isHovered(node));
    let tooltip = null;

    const hideTooltip = () => {
        if (tooltip) {
            tooltip.removeEventListener('mouseenter', onMouseEnter);
            tooltip.removeEventListener('mouseleave', onMouseOut);
            unmountTooltip(tooltip);
            tooltip = null;
        }
    };
    let hideTooltipTimeout: number;

    const hideTooltipAndNode = () => {
        nodes.forEach((node) => {
            node.removeEventListener('mouseenter', onMouseEnter);
            node.removeEventListener('mouseleave', onMouseOut);
        });

        hideTooltip();
        if (onNodeHide) {
            onNodeHide();
        }
    };

    const onMouseOut = () => {
        if (hideTooltipTimeout) {
            clearTimeout(hideTooltipTimeout);
        }
        hideTooltipTimeout = setTimeout(hideTooltipAndNode, 300);
    };

    const onMouseEnter = () => {
        if (hideTooltipTimeout) {
            clearTimeout(hideTooltipTimeout);
            hideTooltipTimeout = null;
        }
    };

    tooltip = mountTooltip(hoveredNode, props, hideTooltipAndNode, direction);

    tooltip.addEventListener('mouseenter', onMouseEnter);
    tooltip.addEventListener('mouseleave', onMouseOut);

    const poll = setInterval(() => {
        if (tooltip) {
            // if node no longer exists in body, remove both
            let n: Node & ParentNode = hoveredNode;
            while (true) {
                if (n === document.body) {
                    break;
                }

                if (!n) {
                    // no parent
                    hideTooltipAndNode();
                    break;
                } else {
                    n = n.parentNode;
                }
            }
        }

        if (!tooltip) {
            clearInterval(poll);
        }
    }, 400);

    nodes.forEach((node) => {
        node.addEventListener('mouseenter', onMouseEnter);
        node.addEventListener('mouseleave', onMouseOut);
    });

    return tooltip;
}

